(5, 1, 2, '手机登录系统', '&lt;p&gt;这是用手机端登录实行的邮件发送。&lt;/p&gt;', '1', 'mmexport1512306240209.jpg', '/Public/upload/2018-02-14/5a842900737a2.jpg', '0', 1518610688),
(13, 1, 5, '分页条数sql查询', '&lt;pre style=&quot;background-color:#ffffff;color:#000000;font-family:&amp;#39;宋体&amp;#39;;font-size:9.0pt;&quot;&gt;select&amp;nbsp;count(*)&amp;nbsp;as&amp;nbsp;count&amp;nbsp;from&amp;nbsp;my_email,my_user&amp;nbsp;&amp;nbsp;where&amp;nbsp;my_email.to_id&amp;nbsp;=&amp;nbsp;my_user.id&amp;nbsp;and&amp;nbsp;my_email.from_id&amp;nbsp;=&amp;#39;.session(&amp;#39;id&amp;#39;)&lt;/pre&gt;&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;&lt;br/&gt;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0', '', '', '0', 1518668405),
(12, 2, 1, '展示发件箱效果的sql语句', '&lt;pre class=&quot;brush:sql;toolbar:false&quot;&gt;select&amp;nbsp;my_email.*,my_user.username,my_user.truename&amp;nbsp;from&amp;nbsp;\r\nmy_email,my_user&amp;nbsp;&amp;nbsp;where&amp;nbsp;my_email.to_id&amp;nbsp;=&amp;nbsp;my_user.id&amp;nbsp;and&amp;nbsp;my_email.from_id\r\n&amp;nbsp;=1&amp;nbsp;order&amp;nbsp;by&amp;nbsp;my_email.id&amp;nbsp;desc;&lt;/pre&gt;&lt;p&gt;&lt;br/&gt;&lt;/p&gt;', '0', '', '', '1', 1518657465),
(15, 2, 1, '分页测试工具', '自我复制功能mysql', '0', '', '', '', 1518657888),
